 ¬† ¬† return¬†true;
 ¬† }

 ¬† auto¬†iter =¬†redirects.find(exception_address);
 ¬† if(iter !=¬†redirects.end())¬†{
 ¬† ¬† // printf("Redirecting...n");
 ¬† ¬† SetRegister(ARCH_PC,¬†iter- >second);
 ¬† ¬† return¬†true;
 ¬† }

 ¬† iter =¬†instrumented_redirects.find(exception_address);
 ¬† if(iter !=¬†instrumented_redirects.end())¬†{
 ¬† ¬† // printf("Redirecting...n");
 ¬† ¬† SetRegister(ARCH_PC,¬†iter- >second);
